select 

from PRM_PARTNER


    and PARTNER_NAME = #{partnerName,jdbcType=VARCHAR}


    and COMPANY_ADDRESS = #{companyAddress,jdbcType=VARCHAR}


    and CONTACT = #{contact,jdbcType=VARCHAR}


    and ORG_ID = #{orgId,jdbcType=BIGINT}


    and IS_ENABLED = #{isEnabled,jdbcType=TINYINT}


    and SALESMAN_ID = #{salesmanId,jdbcType=BIGINT}


    and REMARK = #{remark,jdbcType=VARCHAR}


    and OP_ID = #{opId,jdbcType=BIGINT}


    and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


    and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}


select count(*) 
from PRM_PARTNER
where ID = #{id,jdbcType=BIGINT}


